import React from 'react';
import "./TaskCard.css";

const color = ['red', 'blue', 'orange'];

const checkbox = document.getElementById("checkbox");
const taskCard = document.querySelector(".TaskCard_task");
if (checkbox) {
    checkbox.addEventListener("change", function() {
        if (checkbox.checked) {
            taskCard.classList.add("strike_through");
        } else {
            taskCard.classList.remove("strike_through");
        }
    }); 
} else {
  // The checkbox element does not exist
}

const TaskCard = ({task, priority}) => {
    return (
        <div className='TaskCard' style={{'borderLeft': `${color[priority-1]} 10px solid`}}>
            <div className='TaskCard_task'>{task}</div>
            <button className='TaskCard_button'><i id='' className='fa fa-circle-o'></i></button>
            {/* <input name='checkbox' id='checkbox' type='checkbox'/> */}
        </div>
    );
}

export default TaskCard;
