import React from 'react';
import "./TaskCard.css";

const color = ['red', 'blue', 'orange'];
const checkbox = document.querySelector('input[name="checkbox"]');
const task = document.querySelector('.TaskCard_task');
checkbox.addEventListener('change', () => {
    if (this.checked) {
        task.classList.add('strike_through');
    } else {
        task.classList.remove('strike_through');
    }
});

const TaskCard = ({task, priority}) => {
    return (
        <div className='TaskCard' style={{'borderLeft': `${color[priority-1]} 10px solid`}}>
            <div className='TaskCard_task'>{task}</div>
            <input name='checkbox' type='checkbox'/>
        </div>
    );
}

export default TaskCard;
