import React, { useState } from 'react';
import "./TaskCard.css";

const color = ['red', 'blue', 'orange'];
const TaskCard = ({task, priority}) => {

    const [handleTask, setHandleTask] = useState(false); 

    const handleClick = () => {

    }
    const handleRightClick = () => {

    }

    return (
        <div onClick={()=>handleClick()} onContextMenu={()=>handleRightClick()} id='TaskCard' className={handleTask?'TaskCard TaskDoneCard':'TaskCard'} style={{'borderLeft': `${color[priority-1]} 10px solid`}}>
            <div className='TaskCard_task'>
                <p className={handleTask?'strike_through':''}>{task}</p>
                <sub>03 Jul 2023</sub>
            </div>
            <button onClick={()=>setHandleTask(!handleTask)} className='TaskCard_button'>
                <i className="fa fa-edit"></i>
            </button>
            <button onClick={()=>setHandleTask(!handleTask)} className='TaskCard_button'>
                <i id='TaskCard_button' className={handleTask?'fa fa-check':'fa fa-circle-o'}></i>
            </button>
        </div>
    );
}

export default TaskCard;
