import React, { useState } from 'react';
import "./TaskCard.css";

const [handleTask, setHandleTask] = useState(true); 

const color = ['red', 'blue', 'orange'];

const handleTaskButton = () => {
    const taskCard = document.getElementById('TaskCard');
    if (taskCard.classList.contains('TaskDoneCard')) {
        setHandleTask(true);
    } else {
        setHandleTask(false);
    }
}

const TaskCard = ({task, priority}) => {
    return (
        <div id='TaskCard' className={handleTask?'TaskCard TaskDoneCard':'TaskCard'} style={{'borderLeft': `${color[priority-1]} 10px solid`}}>
            <div className='TaskCard_task'>{task}</div>
            <button onClick={()=>handleTaskButton()} className='TaskCard_button'>
                <i id='TaskCard_button' className='fa fa-circle-o'></i>
            </button>
        </div>
    );
}

export default TaskCard;
