import React from 'react';
import "./TaskCard.css";

const color = ['red', 'blue', 'orange'];

const handleTaskButton = () => {
    const currentClass = document.querySelector('#TaskCard').className;
    const newClass = currentClass === 'TaskCard' ? 'TaskDoneCard' : 'TaskCard';
    document.querySelector('#TaskCard').className = newClass;
}

const TaskCard = ({task, priority}) => {
    return (
        <div id='TaskCard' className='TaskCard' style={{'borderLeft': `${color[priority-1]} 10px solid`}}>
            <div className='TaskCard_task'>{task}</div>
            <button onClick={()=>handleTaskButton()} className='TaskCard_button'><i id='TaskCard_button' className='fa fa-circle-o'></i></button>
            {/* <input name='checkbox' id='checkbox' type='checkbox'/> */}
        </div>
    );
}

export default TaskCard;
