import React, { useState } from 'react';
import './Home.css';

const Home = () => {

    // DATE FORMATER
    const currentDate = new Date();
    const formattedDate = currentDate.toLocaleDateString('en-GB');

    // GOOGLE SEARCH
    const [sentence, setSentence] = useState("");
    const googleSearchURL = "https://www.google.com/search?q=" + encodeURIComponent(sentence).replace(/%20/g, "+"); 
    console.log(googleSearchURL);

    const handleGoogleSearch = () => {
        const newTab = window.open(googleSearchURL, "_blank");
        if (newTab) {
            newTab.focus();
        }
    };

    return (
        <React.Fragment>
            <header>
                <div className="container flex_space_between">
                    <div className="logo"></div>
                    <div className="header_options_wrapper">
                        <a href="https://chat.openai.com" target="_blank"><span className="background_image_contain openai_chatgpt"></span> ChatGPT</a>
                        <a href="https://bard.google.com" target="_blank"><span className="background_image_contain google_bard"></span> Bard</a>
                        <a href="https://www.bing.com/search?q=Bing+AI&showconv=1&FORM=hpcodx" target="_blank"><span className="background_image_contain bing_ai"></span> Bing</a>
                        <div className="user_profile"></div>
                    </div>
                </div>
            </header>

            <article className="Landing">

                <div className="landing_search">    
                    <div className="search_field">
                        <i className="fa fa-search"></i>
                        <input type="text" placeholder="Search here..." value={sentence} onChange={(e) => setSentence(e.target.value)}/>
                        <span className="search_options">
                            <button onClick={()=>handleGoogleSearch} className="background_image_contain search_btn google_search_btn"></button>
                            <button onClick={()=>handleBingSearch} className="background_image_contain search_btn bing_search_btn"></button>
                            <button onClick={()=>handleDuckDuckGOSearch} className="background_image_contain search_btn duckduckgo_search_btn"></button>
                        </span>
                    </div>
                </div>

                <div className="landing_tasks">
                    <div className="tasks_header">
                        <div>
                            <h2>TASK'S TODAY</h2>
                            <p>{formattedDate}</p>
                        </div>
                        <button className='btn btn_primary'><i className="fa fa-plus"></i> NEW</button>
                    </div>
                    <div className="tasks_main"></div>
                </div>

            </article>

            <footer>
                <div className="container">

                </div>
            </footer>
        </React.Fragment>
    );
}

export default Home;
